    client = c.Client("localhost", 12345, 7000)
    # client = c.Client("192.168.0.105", 12345, 2698)
    # s = ""
    # print(client.send_base64(s))

    print(client.register_and_get_auth_token("login", "password"))
    token = client.login_and_get_auth_token("login", "password")
    print(token)
    flight_id = uuid.uuid4()
    label = get_random_string(15)
    license = get_random_string(32)
    r = client.send_flight_state(token, 10, 20, flight_id.bytes, label, license, False)
    print(r)
